let wins = 0
let looses = 0
let count = 0

function getComputerChoice() {
    /*generating a random number between 0 and 2 */
    let random = () => {
    return Math.floor(Math.random() * 3)
    }
    
    /* switch statement to assign rock, paper and scissors strings depending on the random number generated by random()*/
    switch(random()) {
        case 0: 
            return 'Rock'
        case 1:
            return 'Paper'
        case 2:
            return 'Scissors'
    }
}

/* function with the logic to play one round of rock paper and scissors */
function playRound() {
    let computerSelection = getComputerChoice().toLowerCase()
    let playerSelection = prompt('Choose Rock, Paper or Scissors').toLowerCase()

    if(playerSelection === computerSelection) { 
        count++
        console.log('Wins: ' + wins, 'Looses: ' + looses)
        return console.log("This is a tie") , console.log('--------------------')
    } else if (playerSelection === 'rock' && computerSelection === 'scissors') {
        wins++
        count++
        console.log('Wins: ' + wins, 'Looses: ' + looses)
        return console.log('You win! rock beats scissors') , console.log('--------------------')
    } else if (playerSelection === 'paper' && computerSelection === 'rock') {
        wins++
        count++
        console.log('Wins: ' + wins, 'Looses: ' + looses)
        return console.log('You win! paper beats rock') , console.log('--------------------')
    } else if (playerSelection === 'scissors' && computerSelection === 'paper') {
        wins++
        count++
        console.log('Wins: ' + wins, 'Looses: ' + looses)
        return console.log('You win! scissors beats paper') , console.log('--------------------')
    } else {
        count++
        looses++
        console.log('Wins: ' + wins, 'Looses: ' + looses)
        return console.log("You Lose! " + computerSelection + " beats " + playerSelection), console.log('--------------------')
    }
}

/*function to play the game 5 times and added a conditional to add a message when the game ends (fifth round) and a message to announce who wins */
function game() {
    while(count < 5) {
        playRound()
    }

    if(count === 5) {
        console.log('GAME ENDS')
        if(wins === looses){
            console.log('Draw!!')
        } else if(wins > looses) {
            console.log('Player wins!!')
        } else {
            console.log('Computer wins!!')
        }
    }
}

game()
